/**
 * Category Memory
 *
 * COLUMNS
 * (pk) id          : int autoincrement (autogenerated)
 *      title       : string
 *      content     : text
 *      coordinates : geometry.point
 *      reports     : virtual           (virtual column that returns number of reports)
 *      createdAt   : datetime          (autogenerated)
 *      iupdatedAt  : datetime          (autogenerated)
 *
 * ASSOCIATIONS
 * (fk) userID      : int               (autogenerated)
 * (fk) categoryID  : int               (autogenerated)
 */
const { DataTypes } = require('sequelize');
const sequelize = require('../config/db').sequelize;
const Report = require('./report');

const Memory = sequelize.define(
    'Memory',
    {
        title: {
            type: DataTypes.STRING,
            allowNull: false,
            validate: {
                min: {
                    args: 4,
                    msg: 'Content has to be minimum 4 characters',
                },
                max: {
                    args: 255,
                    msg: 'Content has to be maximum 255 characters',
                },
            },
        },
        content: {
            type: DataTypes.TEXT,
            allowNull: false,
            validate: {
                min: {
                    args: 10,
                    msg: 'Content has to be minimum 10 characters',
                },
            },
        },
        position: {
            type: DataTypes.GEOMETRY('POINT'),
            allowNull: false,
        },
        reports: {
            type: DataTypes.VIRTUAL,
            get() {
                console.log(this.id)
                Memory.FindAndCountAll({
                    where: {
                        memoryId: 6,
                    },
                })
                    .then(reports => {
                        console.log("asdf")
                        if (reports.count != 0) {
                            return reports.count;
                        } else {
                            return 0;
                        }
                    })
                    .catch(function(err) {
                        return 0;    
                    });
            return 0;
            },
        },
        user: {
            type: DataTypes.VIRTUAL,
            get () {
                return "asdfa"
            }
        },
    },
    {
        timestamps: true,
    }
);

Memory.associate = function(models) {
    //Memory posted by one user
    Memory.belongsTo(models.User, {
        foreignKey: 'userId',
    });

    //Memory have one category
    Memory.belongsTo(models.Category, {
        foreignKey: 'categoryId',
    });
};

module.exports = Memory;
